package dto;public class CustomerDto {    private String firstName;    private String surname;    LocationDto location;    public String getFirstName() {        return firstName;    }    public void setFirstName(String firstName) {        this.firstName = firstName;    }    public String getSurname() {        return surname;    }    public void setSurname(String surname) {        this.surname = surname;    }    public LocationDto getLocation() {        return location;    }    public void setLocation(LocationDto location) {        this.location = location;    }    @Override    public String toString() {        return "CustomerDto{" +          "firstName='" + firstName + '\'' +          ", surname='" + surname + '\'' +          ", location=" + location +          '}';    }}