package rest;import dto.CustomerDto;import service.AbstractService;import javax.inject.Inject;import javax.inject.Named;import javax.ws.rs.GET;import javax.ws.rs.Path;import javax.ws.rs.PathParam;import javax.ws.rs.Produces;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;import java.util.logging.Level;import java.util.logging.Logger;@Path("")public class CustomerWs  extends BaseWs implements CustomerWsApi{    private static final Logger LOGGER = Logger.getLogger(CustomerWs.class.getName());    @Inject    @Named("customerService")    private AbstractService<CustomerDto, String> customerService;    @GET    @Path("customer")    @Produces(MediaType.APPLICATION_JSON)    @Override    public Response getCustomers()    {        LOGGER.log(Level.INFO, "Invoked method getCustomers");        return this.execute(this.customerService);    }}